mapper.select:
SELECT
-- 级联表的字段 ,nba.nba_name as nbaName ,nba.nba_photo as nbaPhoto ,nba.nba_video as nbaVideo ,nba.nba_types as nbaTypes ,nba.nba_clicknum as nbaClicknum ,nba.shangxia_types as shangxiaTypes ,nba.nba_delete as nbaDelete ,nba.nba_content as nbaContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_xingming as yonghuXingming ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto
FROM nba_liuyan a left JOIN nba nba ON a.nba_id = nba.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.nba_id = #{params.nbaId}
and a.yonghu_id = #{params.yonghuId}
and a.nba_liuyan_text like CONCAT('%',#{params.nbaLiuyanText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and nba.nba_name like CONCAT('%',#{params.nbaName},'%')
and nba.nba_video like CONCAT('%',#{params.nbaVideo},'%')
and nba.nba_types = #{params.nbaTypes}
and nba.nba_clicknum >= #{params.nbaClicknumStart}
and nba.nba_clicknum <= #{params.nbaClicknumEnd}
and nba.nba_clicknum = #{params.nbaClicknum}
and nba.shangxia_types = #{params.shangxiaTypes}
and nba.nba_delete >= #{params.nbaDeleteStart}
and nba.nba_delete <= #{params.nbaDeleteEnd}
and nba.nba_delete = #{params.nbaDelete}
and nba.nba_content like CONCAT('%',#{params.nbaContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_xingming like CONCAT('%',#{params.yonghuXingming},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
order by a.${params.orderBy} desc
