mapper.select:
SELECT
-- 级联表的字段 ,papi.papi_name as papiName ,papi.papi_photo as papiPhoto ,papi.papi_video as papiVideo ,papi.papi_types as papiTypes ,papi.papi_clicknum as papiClicknum ,papi.shangxia_types as shangxiaTypes ,papi.papi_delete as papiDelete ,papi.papi_content as papiContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_xingming as yonghuXingming ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto
FROM papi_liuyan a left JOIN papi papi ON a.papi_id = papi.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.papi_id = #{params.papiId}
and a.yonghu_id = #{params.yonghuId}
and a.papi_liuyan_text like CONCAT('%',#{params.papiLiuyanText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and papi.papi_name like CONCAT('%',#{params.papiName},'%')
and papi.papi_video like CONCAT('%',#{params.papiVideo},'%')
and papi.papi_types = #{params.papiTypes}
and papi.papi_clicknum >= #{params.papiClicknumStart}
and papi.papi_clicknum <= #{params.papiClicknumEnd}
and papi.papi_clicknum = #{params.papiClicknum}
and papi.shangxia_types = #{params.shangxiaTypes}
and papi.papi_delete >= #{params.papiDeleteStart}
and papi.papi_delete <= #{params.papiDeleteEnd}
and papi.papi_delete = #{params.papiDelete}
and papi.papi_content like CONCAT('%',#{params.papiContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_xingming like CONCAT('%',#{params.yonghuXingming},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
order by a.${params.orderBy} desc
