mapper.select:
SELECT
-- 级联表的字段 ,tiyu.tiyu_name as tiyuName ,tiyu.tiyu_photo as tiyuPhoto ,tiyu.tiyu_video as tiyuVideo ,tiyu.tiyu_types as tiyuTypes ,tiyu.tiyu_clicknum as tiyuClicknum ,tiyu.shangxia_types as shangxiaTypes ,tiyu.tiyu_delete as tiyuDelete ,tiyu.tiyu_content as tiyuContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_xingming as yonghuXingming ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto
FROM tiyu_liuyan a left JOIN tiyu tiyu ON a.tiyu_id = tiyu.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.tiyu_id = #{params.tiyuId}
and a.yonghu_id = #{params.yonghuId}
and a.tiyu_liuyan_text like CONCAT('%',#{params.tiyuLiuyanText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and tiyu.tiyu_name like CONCAT('%',#{params.tiyuName},'%')
and tiyu.tiyu_video like CONCAT('%',#{params.tiyuVideo},'%')
and tiyu.tiyu_types = #{params.tiyuTypes}
and tiyu.tiyu_clicknum >= #{params.tiyuClicknumStart}
and tiyu.tiyu_clicknum <= #{params.tiyuClicknumEnd}
and tiyu.tiyu_clicknum = #{params.tiyuClicknum}
and tiyu.shangxia_types = #{params.shangxiaTypes}
and tiyu.tiyu_delete >= #{params.tiyuDeleteStart}
and tiyu.tiyu_delete <= #{params.tiyuDeleteEnd}
and tiyu.tiyu_delete = #{params.tiyuDelete}
and tiyu.tiyu_content like CONCAT('%',#{params.tiyuContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_xingming like CONCAT('%',#{params.yonghuXingming},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
order by a.${params.orderBy} desc
