mapper.select:
SELECT
-- 级联表的字段 ,yishu.yishu_name as yishuName ,yishu.yishu_photo as yishuPhoto ,yishu.yishu_video as yishuVideo ,yishu.yishu_types as yishuTypes ,yishu.yishu_clicknum as yishuClicknum ,yishu.shangxia_types as shangxiaTypes ,yishu.yishu_delete as yishuDelete ,yishu.yishu_content as yishuContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_xingming as yonghuXingming ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto
FROM yishu_collection a left JOIN yishu yishu ON a.yishu_id = yishu.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.yishu_id = #{params.yishuId}
and a.yonghu_id = #{params.yonghuId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and yishu.yishu_name like CONCAT('%',#{params.yishuName},'%')
and yishu.yishu_video like CONCAT('%',#{params.yishuVideo},'%')
and yishu.yishu_types = #{params.yishuTypes}
and yishu.yishu_clicknum >= #{params.yishuClicknumStart}
and yishu.yishu_clicknum <= #{params.yishuClicknumEnd}
and yishu.yishu_clicknum = #{params.yishuClicknum}
and yishu.shangxia_types = #{params.shangxiaTypes}
and yishu.yishu_delete >= #{params.yishuDeleteStart}
and yishu.yishu_delete <= #{params.yishuDeleteEnd}
and yishu.yishu_delete = #{params.yishuDelete}
and yishu.yishu_content like CONCAT('%',#{params.yishuContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_xingming like CONCAT('%',#{params.yonghuXingming},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
order by a.${params.orderBy} desc
