mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_xingming as yonghuXingming ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yule.yule_name as yuleName ,yule.yule_photo as yulePhoto ,yule.yule_video as yuleVideo ,yule.yule_types as yuleTypes ,yule.yule_clicknum as yuleClicknum ,yule.shangxia_types as shangxiaTypes ,yule.yule_delete as yuleDelete ,yule.yule_content as yuleContent
FROM yule_collection a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN yule yule ON a.yule_id = yule.id
and a.id in
#{item}
and a.yule_id = #{params.yuleId}
and a.yonghu_id = #{params.yonghuId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_xingming like CONCAT('%',#{params.yonghuXingming},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yule.yule_name like CONCAT('%',#{params.yuleName},'%')
and yule.yule_video like CONCAT('%',#{params.yuleVideo},'%')
and yule.yule_types = #{params.yuleTypes}
and yule.yule_clicknum >= #{params.yuleClicknumStart}
and yule.yule_clicknum <= #{params.yuleClicknumEnd}
and yule.yule_clicknum = #{params.yuleClicknum}
and yule.shangxia_types = #{params.shangxiaTypes}
and yule.yule_delete >= #{params.yuleDeleteStart}
and yule.yule_delete <= #{params.yuleDeleteEnd}
and yule.yule_delete = #{params.yuleDelete}
and yule.yule_content like CONCAT('%',#{params.yuleContent},'%')
order by a.${params.orderBy} desc
